#include "KeyInterruption.hpp"
#include "Timer.hpp"
#include "sfr_r829.h"

#define BUTTON_PULLUP


void _wait_us(unsigned int us){
	unsigned int tm = us * 20;
	for (int i = 0; i < tm; ++i){
		asm("nop");
	}
}

void _wait_ms(unsigned int ms){
	for (int i = 0; i < ms; ++i){
		_wait_us(1000);
	}
}


class INTRclass{
public:
	INTRbase *ib;
	void run(){
		ib->op();
	}
};

