void KeyInterruption::Enable(){

	////割り込み許可
	//pol_int1ic = 0;
	//pol_int3ic = 0;


#ifdef BUTTON_PULLUP
	pol_int1ic = 0;
	pol_int3ic = 0;
#else
	pol_int1ic = 1;
	pol_int3ic = 1;
#endif

	ilvl0_int1ic = 0;
	ilvl1_int1ic = 1;
	ilvl2_int1ic = 1;

	ilvl0_int3ic = 0;
	ilvl1_int3ic = 1;
	ilvl2_int3ic = 1;

	//割り込み許可
	ilvl0_kupic = 0;
	ilvl1_kupic = 1;
	ilvl2_kupic = 1;


	//割り込み許可
	asm("FSET I");
}

void KeyInterruption::SetEventMode(INTRbase * pib){
	int_mode.ib = pib;	
}

void KeyInterruption::SetEventSelect(INTRbase * pib){
	int_sel.ib = pib;
}

void KeyInterruption::SetEventUp(INTRbase * pib){
	int_up.ib = pib;
}

void KeyInterruption::SetEventDown(INTRbase * pib){
	int_down.ib = pib;
}

#pragma INTERRUPT t_intr_mode (vect=25)
void t_intr_mode(){
	//入力禁止
	int1en = 0;

	_wait_ms(10);
	int_mode.run();

	//入力許可
	int1en = 1;
}

#pragma INTERRUPT t_intr_sel (vect=26)
void t_intr_sel(){
	//入力禁止
	int3en = 0;

	_wait_ms(10);
	int_sel.run();

	//入力許可
	int3en = 1;
}

//立ち上がりで発生とする
//プルアップしてるときは立下りにする
#pragma INTERRUPT t_intr_key (vect=13)
void t_intr_key(){
	//入力禁止
	ki0en = 0;
	ki1en = 0;
#ifdef BUTTON_PULLUP

	_wait_ms(10);
	if (p1_0 == 0){
		int_up.run();
		return;
	} else if (p1_1 == 0){
		int_down.run();
		return;
	}

#else


	wait_ms(10);
	if (p1_0 == 1){
		int_up.run();
		return;
	}
	else if (p1_1 == 1){
		int_down.run();
		return;
	}

#endif

	////入力禁止
	//ki0en = 0;
	//ki1en = 0;

	//wait_ms(10);
	//if (p1_0 == 1){
	//	int_up.run();
	//	return;
	//} else if (p1_1 == 1){
	//	int_down.run();
	//	return;
	//}

	//入力許可
	ki0en = 1;
	ki1en = 1;
}















